import React, { Component } from 'react';
import './sept.css';
import ProfileService from '../../api/sept/ProfileService.js';
import AppMenu from './AppMenu';

class Profile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            sid: 0,
            course: '',
            bio: '',
            isInEditMode: false
        };
    }


    componentDidMount() {
        // Create dummy profile: (comment out after testing)
        // this.setState({
        //     name: 'Eyup Keskin',
        //     sid: 3686150,
        //     course: 'SoftEng',
        //     bio: 'I am very boring'
        // })

        // Retrieve profile
        ProfileService.retrieveProfile(3686150)
            .then(
                response => {
                    this.setState({
                        name: response.data.name,
                        sid: response.data.sid,
                        course: response.data.course,
                        bio: response.data.bio
                    })
                }
            )
    }

    changeEditMode = () => {
        this.setState({isInEditMode: !this.state.isInEditMode})
    }

    renderEditView = () => {
        return <div>
            <input
                type='text'
                defaultValue={this.state.bio}
            />
            <button onClick={this.changeEditMode}>X</button>
        </div>
    }

    renderDefaulfView = () => {
        return <div onDoubleClick={this.changeEditMode}>
            {this.state.bio}
        </div>
    }

    render(){

        return this.state.isInEditMode ?
        this.renderEditView():
        this.renderDefaulfView()
    }

    render() {
        let links = [
            { label: 'Home', link: '/index', },
            { label: 'Chat', link: '/index', },
            { label: 'User Posts', link: '/posts', },
            { label: 'Profile', link: '/profile', active: true },
            { label: 'Logout', link: '/logout' },
        ];

        return(
            <div>
                <div className="container center">
                    <AppMenu links={links} history={this.props.history} />
                </div>

                <div className="Profile">
                    return this.state.isInEditMode ?
                    this.renderEditView():
                    this.renderDefaulfView()
                    <br/>
                </div>
            </div>
        );
    }

}

export default Profile;
